    private void EliminaxCodActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_EliminaxCodActionPerformed
        // TODO add your handling code here:
        int x = -1;
        try {
            x = Integer.parseInt(LeeCodEliminar.getText());
        } catch (Exception e) {
            txElimina.setText("sintaxis Codigo Erronea");
        }
        if (x != -1) {
            boolean borrado = plantel.BorrarXcodigo(x);
            if (borrado == true) {
                txElimina.setText("MIEMBRO ELIMINADO");
            } else {
                txElimina.setText("Miembro no encontrado");
            }

        }

    }//GEN-LAST:event_EliminaxCodActionPerformed

