    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        txAreaAD.setText("");
        if (valida.validaNombre(LeeNombreAD.getText()) == false) {
            txAreaAD.append("DEBE INGRESAR UN NOMBRE VALIDO");
            LeeNombreAD.requestFocus();
            return;
        }
        if (LeeRutAD.getText().length() != 0) {
            if (plantel.verificaRut(LeeRutAD.getText()) == false) {
                if (valida.ValidaRut(LeeRutAD.getText()) == false) {
                    txAreaAD.append("RUT NO VALIDO (DIGITO Y GUION)");
                    LeeRutAD.requestFocus();
                    return;
                }
            } else {
                txAreaAD.append("RUT PERTENECE A OTRO MIEMBRO");
                LeeRutAD.requestFocus();
                return;
            }

        } else {
            txAreaAD.append("DEBE INGRESAR UN RUT");
            return;
        }

        int edad = 0;
        try {
            edad = Integer.parseInt(LeeEdadAD.getText());
        } catch (Exception e) {
            txAreaAD.append("EDAD NO VALIDO");
            return;
        }
        if (valida.validaEdad(edad) == false) {
            txAreaAD.append("Rango de edad no valido");
            LeeEdadAD.requestFocus();
            return;
        }
        float sueldo;
        try {
            sueldo = Float.parseFloat(LeeSueldoAD.getText());
        } catch (Exception e) {
            txAreaAD.append("SUELDO NO VALIDO");
            return;
        }
        plantel.addAdministrativo(LeeNombreAD.getText(), LeeRutAD.getText(), edad, sueldo);
        txAreaAD.append("ADMINISTRATIVO CREADO");

    }//GEN-LAST:event_jButton1ActionPerformed

