    private void MUESTRALISTAActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_MUESTRALISTAActionPerformed
        // boton mostrar lista
//
//
//
//
        TxArea.setText("");
        String ln = System.getProperty("line.separator");
        ArrayList<String> muestra = new ArrayList<String>();
        if (comboListas.getSelectedItem().equals("TODOS")) {
            plantel.Listar(muestra);
        }
        if (comboListas.getSelectedItem().equals("ADMINISTRATIVOS")) {
            plantel.MostrarLista(1, muestra);
        }
        if (comboListas.getSelectedItem().equals("DOCENTES DE PLANTA")) {
            plantel.MostrarLista(2, muestra);
        }
        if (comboListas.getSelectedItem().equals("DOCENTES A HONORARIOS")) {
            plantel.MostrarLista(3, muestra);
        }
        if (comboListas.getSelectedItem().equals("ALUMNADO")) {
            plantel.MostrarLista(4, muestra);
        }
        for (int i = 0; i < muestra.size(); i++) {
            TxArea.append(muestra.get(i));
            TxArea.append(ln);
        }

    }//GEN-LAST:event_MUESTRALISTAActionPerformed

    private void BuscaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BuscaActionPerformed
        // boton busca por codigo o asig
        /**
         * combobox: Seleccione por Codigo Miembro por Codigo Asignatura
         * (DOCENTES)
         */
        TxArea.setText("");
        String ln = System.getProperty("line.separator");
        ArrayList<String> muestra = new ArrayList<String>();
        int code = -1;
        try {
            code = Integer.parseInt(LeeBuscaxTipo.getText());
        } catch (Exception e) {
            TxArea.append("DEBE SER UN CODIGO ENTERO");
            code = -1;
            TxArea.append(ln);
        }

        if (comboBuscaxTipo.getSelectedItem().equals("Seleccione")) {
            TxArea.append("DEBE SELECCIONAR UN TIPO");
            comboBuscaxTipo.requestFocus();
        }
        if (comboBuscaxTipo.getSelectedItem().equals("por Codigo Miembro")) {
            if (code != -1) {
                muestra = plantel.BuscaxCodigo(code, muestra);
            }
        }
        if (comboBuscaxTipo.getSelectedItem().equals("por Codigo Asignatura (DOCENTES)")) {
            if (code != -1) {
                muestra = plantel.BuscarxAsig(code, muestra);
            }
        }
        for (int i = 0; i < muestra.size(); i++) {
            TxArea.append(muestra.get(i));
            TxArea.append(ln);
        }

    }//GEN-LAST:event_BuscaActionPerformed

